async function main() {
  if (PROJECT_KEY.includes("YOUR")) {
    console.error("\nâŒ Error: Please fill in your PROJECT_KEY in the script.\n");
    return;
  }
  
  console.log("ðŸš€ Starting deployment script with authentication...");

  // 1. åˆ›å»ºä¸€ä¸ªè‡ªå®šä¹‰çš„è¯·æ±‚å¯¹è±¡ (FetchRequest)
  // è¿™æ˜¯ä¸ºäº†èƒ½å¤Ÿæ·»åŠ æˆ‘ä»¬è‡ªå®šä¹‰çš„ x-api-key header
  const fetchRequest = new ethers.FetchRequest(RPC_URL);
  fetchRequest.setHeader("x-api-key", PROJECT_KEY);
  console.log("âœ… Custom request header 'x-api-key' prepared.");
  
  // 2. ä½¿ç”¨è¿™ä¸ªè‡ªå®šä¹‰çš„è¯·æ±‚å¯¹è±¡æ¥åˆå§‹åŒ–æˆ‘ä»¬çš„Provider
  // è¿™æ ·ï¼Œetherså‘é€çš„æ¯ä¸€ä¸ªè¯·æ±‚éƒ½ä¼šè‡ªåŠ¨å¸¦ä¸Šæˆ‘ä»¬çš„header
  const provider = new ethers.JsonRpcProvider(fetchRequest);
  console.log("âœ… Connected to BSN Gateway with authentication.");

  // 3. åŽç»­æ­¥éª¤ä¸Žä¹‹å‰å®Œå…¨ç›¸åŒ
  const signer = new ethers.Wallet(PRIVATE_KEY, provider);
  console.log("âœ… Using deployment account:", signer.address);

  const ContractFactory = new ethers.ContractFactory(contractABI, contractBytecode, signer);
  console.log("\nâ³ Deploying contract, please wait...");

  try {
    const contract = await ContractFactory.deploy();
    await contract.waitForDeployment();
    const contractAddress = await contract.getAddress();

    console.log("\n==========================================================");
    console.log("ðŸŽ‰ SUCCESS! Contract deployed successfully!");
    console.log("ðŸ“œ Contract Address:", contractAddress);
    console.log("==========================================================");

  } catch (error) {
    console.error("\nâŒ DEPLOYMENT FAILED!");
    console.error("Error details:", error); // æ‰“å°å®Œæ•´çš„é”™è¯¯å¯¹è±¡ä»¥èŽ·å–æ›´å¤šä¿¡æ¯
  }
}

