// ====================================================================================
// ========================== DEPLOYMENT SCRIPT - 下面的代码无需修改 ======================
// ====================================================================================

async function main() {
  if (PROJECT_KEY.includes("YOUR")) {
    console.error("\n❌ Error: Please fill in your PROJECT_KEY in the script.\n");
    return;
  }
  
  console.log("🚀 Starting deployment script with authentication...");

  // 1. 创建一个自定义的请求对象 (FetchRequest)
  // 这是为了能够添加我们自定义的 x-api-key header
  const fetchRequest = new ethers.FetchRequest(RPC_URL);
  fetchRequest.setHeader("x-api-key", PROJECT_KEY);
  console.log("✅ Custom request header 'x-api-key' prepared.");
  
  // 2. 使用这个自定义的请求对象来初始化我们的Provider
  // 这样，ethers发送的每一个请求都会自动带上我们的header
  const provider = new ethers.JsonRpcProvider(fetchRequest);
  console.log("✅ Connected to BSN Gateway with authentication.");

  // 3. 后续步骤与之前完全相同
  const signer = new ethers.Wallet(PRIVATE_KEY, provider);
  console.log("✅ Using deployment account:", signer.address);

  const ContractFactory = new ethers.ContractFactory(contractABI, contractBytecode, signer);
  console.log("\n⏳ Deploying contract, please wait...");

  try {
    const contract = await ContractFactory.deploy();
    await contract.waitForDeployment();
    const contractAddress = await contract.getAddress();

    console.log("\n==========================================================");
    console.log("🎉 SUCCESS! Contract deployed successfully!");
    console.log("📜 Contract Address:", contractAddress);
    console.log("==========================================================");

  } catch (error) {
    console.error("\n❌ DEPLOYMENT FAILED!");
    console.error("Error details:", error); // 打印完整的错误对象以获取更多信息
  }
}

main().catch((error) => {
  console.error(error);
  process.exitCode = 1;
});